using System.Collections;using System.Collections.Generic;using UnityEngine;public class MoveCamera : MonoBehaviour {    // Use this for initialization    void Start () {    }		// Update is called once per frame	void Update ()    {        if (Input.GetAxis("Mouse ScrollWheel") < 0) // bakcward        {            Camera.main.orthographicSize = Mathf.Min(Camera.main.orthographicSize + 1, 27);        }        if (Input.GetAxis("Mouse ScrollWheel") > 0) // forward        {            Camera.main.orthographicSize = Mathf.Max(Camera.main.orthographicSize - 1, 7);        }            }}